import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { motion } from "framer-motion";
import { 
  Form, 
  FormControl, 
  FormField, 
  FormItem, 
  FormLabel, 
  FormMessage 
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Department, insertDepartmentSchema } from "@shared/schema";
import { 
  Loader2, 
  Building2, 
  Users, 
  FileText, 
  CheckCircle2,
  Target,
  Briefcase
} from "lucide-react";
import { cn } from "@/lib/utils";

interface DepartmentFormProps {
  department?: Department;
  onSuccess: () => void;
}

export function DepartmentForm({ department, onSuccess }: DepartmentFormProps) {
  const { toast } = useToast();
  const isEditing = !!department;

  // Create form schema
  const formSchema = insertDepartmentSchema;

  type FormValues = z.infer<typeof formSchema>;

  // Set up form with default values
  const form = useForm<FormValues>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: department?.name || "",
      description: department?.description || "",
    },
  });

  // Create or update department mutation
  const mutation = useMutation({
    mutationFn: async (values: FormValues) => {
      if (isEditing) {
        return await apiRequest(
          "PUT", 
          `/api/departments/${department.id}`, 
          values
        );
      } else {
        return await apiRequest("POST", "/api/departments", values);
      }
    },
    onSuccess: () => {
      toast({
        title: isEditing ? "Department updated" : "Department created",
        description: isEditing 
          ? "Department information has been updated successfully." 
          : "New department has been created successfully.",
      });
      onSuccess();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Form submission handler
  const onSubmit = (values: FormValues) => {
    mutation.mutate(values);
  };

  return (
    <div className="form-container">
      {/* Executive Header */}
      <div className="bg-gradient-to-r from-slate-50 via-slate-50 to-white -mx-6 -mt-6 px-6 pt-6 pb-6 mb-6 border-b-2 border-slate-200 shadow-sm">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-2xl font-bold bg-gradient-to-r from-slate-900 to-slate-700 bg-clip-text text-transparent mb-2">
              {isEditing ? "Update Department" : "Create New Department"}
            </h2>
            <p className="text-slate-600 text-sm">
              {isEditing 
                ? "Modify department information and organizational structure" 
                : "Set up a new department to organize your workforce"}
            </p>
          </div>
          <div className="hidden sm:block">
            <div className="bg-gradient-to-br from-teal-100 to-teal-200 p-4 rounded-xl shadow-sm">
              <Building2 className="w-8 h-8 text-teal-700" />
            </div>
          </div>
        </div>
      </div>

      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="bg-white rounded-2xl border-2 border-slate-200 shadow-lg hover:shadow-xl transition-shadow duration-300"
          >
            <div className="bg-gradient-to-r from-slate-50 via-white to-slate-50 px-6 py-5 rounded-t-2xl border-b-2 border-slate-100">
              <h3 className="text-xl font-bold text-slate-900 flex items-center">
                <div className="bg-gradient-to-br from-teal-100 to-teal-200 p-3 rounded-xl mr-4 shadow-sm">
                  <Briefcase className="w-5 h-5 text-teal-700" />
                </div>
                Department Information
              </h3>
              <p className="text-sm text-slate-600 mt-2 ml-12 font-medium">Basic details and organizational structure</p>
            </div>

            <div className="p-6 space-y-6">
              {/* Department Name Field */}
              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-bold text-slate-700 mb-2 block flex items-center">
                      <Building2 className="w-4 h-4 mr-2 text-teal-600" />
                      Department Name *
                    </FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Input 
                          placeholder="e.g., Human Resources, Engineering, Marketing" 
                          className="h-12 border-2 border-slate-200 focus:border-teal-500 focus:ring-2 focus:ring-teal-100 rounded-lg text-sm font-medium transition-all duration-200 pl-4"
                          {...field} 
                        />
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Description Field */}
              <FormField
                control={form.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-bold text-slate-700 mb-2 block flex items-center">
                      <FileText className="w-4 h-4 mr-2 text-teal-600" />
                      Department Description
                    </FormLabel>
                    <FormControl>
                      <Textarea 
                        placeholder="Describe the department's role, responsibilities, and objectives within the organization..." 
                        value={field.value || ""}
                        onChange={field.onChange}
                        onBlur={field.onBlur}
                        name={field.name} 
                        className="resize-none border-2 border-slate-200 focus:border-teal-500 focus:ring-2 focus:ring-teal-100 rounded-lg text-sm font-medium transition-all duration-200 min-h-[120px]"
                        rows={5}
                      />
                    </FormControl>
                    <FormMessage />
                    <div className="text-xs text-slate-500 mt-2 flex items-center">
                      <Target className="w-3 h-3 mr-1" />
                      Include department goals, key functions, and team structure
                    </div>
                  </FormItem>
                )}
              />

              {/* Department Features Info */}
              <div className="bg-gradient-to-br from-blue-50 to-blue-100 rounded-xl p-5 border border-blue-200">
                <div className="flex items-start space-x-3">
                  <div className="bg-blue-500 p-2 rounded-lg">
                    <Users className="w-5 h-5 text-white" />
                  </div>
                  <div className="flex-1">
                    <h4 className="text-sm font-bold text-blue-900 mb-2">Department Benefits</h4>
                    <div className="space-y-2 text-sm text-blue-800">
                      <div className="flex items-center space-x-2">
                        <CheckCircle2 className="w-4 h-4 text-blue-600" />
                        <span>Organize employees into logical groups</span>
                      </div>
                      <div className="flex items-center space-x-2">
                        <CheckCircle2 className="w-4 h-4 text-blue-600" />
                        <span>Streamline reporting and management structure</span>
                      </div>
                      <div className="flex items-center space-x-2">
                        <CheckCircle2 className="w-4 h-4 text-blue-600" />
                        <span>Enable better resource allocation and planning</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </motion.div>

          {/* Form Navigation */}
          <div className="bg-white border-2 border-slate-200 shadow-lg px-6 py-4 rounded-lg">
            <div className="flex flex-col-reverse sm:flex-row sm:justify-between sm:space-x-4 space-y-3 space-y-reverse sm:space-y-0">
              <div className="flex space-x-3">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={onSuccess}
                  className="w-full sm:w-auto h-11 px-6 border-2 border-slate-300 hover:border-slate-400 font-medium transition-all duration-200"
                >
                  Cancel
                </Button>
              </div>

              <div className="flex space-x-3">
                <Button 
                  type="submit"
                  className={cn(
                    "w-full sm:w-auto h-11 px-8 font-semibold transition-all duration-200 shadow-lg hover:shadow-xl",
                    "bg-gradient-to-r from-teal-600 via-teal-600 to-emerald-600 hover:from-teal-700 hover:via-teal-700 hover:to-emerald-700"
                  )}
                  disabled={mutation.isPending}
                >
                  {mutation.isPending && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                  {!mutation.isPending && (
                    <Building2 className="mr-2 h-4 w-4" />
                  )}
                  {isEditing ? "Update Department" : "Create Department"}
                </Button>
              </div>
            </div>
          </div>
        </form>
      </Form>
    </div>
  );
}

client/src/components/employees/employee.content:
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { 
  Form, 
  FormControl, 
  FormField, 
  FormItem, 
  FormLabel, 
  FormMessage 
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { User, Department, insertUserSchema } from "@shared/schema";
import { Loader2 } from "lucide-react";

interface EmployeeFormProps {
  employee?: User;
  departments: Department[];
  onSuccess: () => void;
}

export function EmployeeForm({ employee, departments, onSuccess }: EmployeeFormProps) {
  const { toast } = useToast();
  const isEditing = !!employee;

  // Create form schema extending the insertUserSchema
  // Password is required for new employees but optional when editing
  const formSchema = z.object({
    username: z.string().min(3, "Username must be at least 3 characters"),
    password: isEditing 
      ? z.string().min(6, "Password must be at least 6 characters").optional().or(z.literal(''))
      : z.string().min(6, "Password must be at least 6 characters"),
    email: z.string().email("Invalid email address"),
    firstName: z.string().min(1, "First name is required"),
    lastName: z.string().min(1, "Last name is required"),
    role: z.enum(['admin', 'hr', 'manager', 'employee']),
    departmentId: z.number().nullable(),
    position: z.string().optional(),
    phoneNumber: z.string().optional(),
    address: z.string().optional(),
    isActive: z.boolean().default(true),
  });

  type FormValues = z.infer<typeof formSchema>;

  // Set up form with default values
  const form = useForm<FormValues>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      username: employee?.username || "",
      password: "",
      email: employee?.email || "",
      firstName: employee?.firstName || "",
      lastName: employee?.lastName || "",
      role: employee?.role || "employee",
      departmentId: employee?.departmentId || null,
      position: employee?.position || "",
      phoneNumber: employee?.phoneNumber || "",
      address: employee?.address || "",
      isActive: employee?.isActive ?? true,
    },
  });

  // Create or update employee mutation
  const mutation = useMutation({
    mutationFn: async (values: FormValues) => {
      // Remove empty password when editing
      if (isEditing && !values.password) {
        const { password, ...dataWithoutPassword } = values;
        return await apiRequest(
          "PUT", 
          `/api/employees/${employee.id}`, 
          dataWithoutPassword
        );
      }

      if (isEditing) {
        return await apiRequest(
          "PUT", 
          `/api/employees/${employee.id}`, 
          values
        );
      } else {
        return await apiRequest("POST", "/api/register", values);
      }
    },
    onSuccess: () => {
      toast({
        title: isEditing ? "Employee updated" : "Employee created",
        description: isEditing 
          ? "Employee information has been updated successfully." 
          : "New employee has been created successfully.",
      });
      onSuccess();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Form submission handler
  const onSubmit = (values: FormValues) => {
    mutation.mutate(values);
  };

  return (
    <div className="max-h-[80vh] overflow-y-auto px-1">
      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
          {/* Personal Information Section */}
          <div className="space-y-4">
            <div className="border-b border-slate-200 pb-2">
              <h3 className="text-lg font-medium text-slate-900">Personal Information</h3>
              <p className="text-sm text-slate-500">Basic details about the employee</p>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="firstName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-slate-700">First Name *</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="Enter first name" 
                        className="h-10 border-slate-300 focus:border-teal-500 focus:ring-teal-500"
                        {...field} 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="lastName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-slate-700">Last Name *</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="Enter last name" 
                        className="h-10 border-slate-300 focus:border-teal-500 focus:ring-teal-500"
                        {...field} 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-slate-700">Email Address *</FormLabel>
                  <FormControl>
                    <Input 
                      type="email" 
                      placeholder="Enter email address" 
                      className="h-10 border-slate-300 focus:border-teal-500 focus:ring-teal-500"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="phoneNumber"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-slate-700">Phone Number</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="Enter phone number" 
                      className="h-10 border-slate-300 focus:border-teal-500 focus:ring-teal-500"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="address"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-slate-700">Address</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Enter full address" 
                      className="min-h-[80px] resize-none border-slate-300 focus:border-teal-500 focus:ring-teal-500"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          {/* Account Information Section */}
          <div className="space-y-4">
            <div className="border-b border-slate-200 pb-2">
              <h3 className="text-lg font-medium text-slate-900">Account Information</h3>
              <p className="text-sm text-slate-500">Login credentials and access details</p>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="username"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-slate-700">Username *</FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="Enter username" 
                        className="h-10 border-slate-300 focus:border-teal-500 focus:ring-teal-500"
                        {...field} 
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-slate-700">
                      {isEditing ? "New Password (optional)" : "Password *"}
                    </FormLabel>
                    <FormControl>
                      <Input 
                        type="password" 
                        placeholder={isEditing ? "Leave blank to keep current" : "Enter password"} 
                        className="h-10 border-slate-300 focus:border-teal-500 focus:ring-teal-500"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>
          </div>

          {/* Organization Information Section */}
          <div className="space-y-4">
            <div className="border-b border-slate-200 pb-2">
              <h3 className="text-lg font-medium text-slate-900">Organization Details</h3>
              <p className="text-sm text-slate-500">Role, department, and position information</p>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="role"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-slate-700">Role *</FormLabel>
                    <Select 
                      value={field.value} 
                      onValueChange={(value) => field.onChange(value)}
                    >
                      <FormControl>
                        <SelectTrigger className="h-10 border-slate-300 focus:border-teal-500 focus:ring-teal-500">
                          <SelectValue placeholder="Select role" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="admin">Admin</SelectItem>
                        <SelectItem value="hr">HR</SelectItem>
                        <SelectItem value="manager">Manager</SelectItem>
                        <SelectItem value="employee">Employee</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="departmentId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-sm font-medium text-slate-700">Department</FormLabel>
                    <Select 
                      value={field.value?.toString() || "none"} 
                      onValueChange={(value) => field.onChange(value === "none" ? null : parseInt(value))}
                    >
                      <FormControl>
                        <SelectTrigger className="h-10 border-slate-300 focus:border-teal-500 focus:ring-teal-500">
                          <SelectValue placeholder="Select department" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="none">No Department</SelectItem>
                        {departments.map((department) => (
                          <SelectItem key={department.id} value={department.id.toString()}>
                            {department.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="position"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-sm font-medium text-slate-700">Position/Job Title</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="Enter position or job title" 
                      className="h-10 border-slate-300 focus:border-teal-500 focus:ring-teal-500"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {isEditing && (
              <FormField
                control={form.control}
                name="isActive"
                render={({ field }) => (
                  <FormItem>
                    <div className="flex items-center justify-between rounded-lg border border-slate-200 p-4 bg-slate-50">
                      <div className="space-y-1">
                        <FormLabel className="text-sm font-medium text-slate-700">Account Status</FormLabel>
                        <p className="text-xs text-slate-500">
                          {field.value ? "Employee account is active" : "Employee account is disabled"}
                        </p>
                      </div>
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                    </div>
                  </FormItem>
                )}
              />
            )}
          </div>

          {/* Form Actions */}
          <div className="flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-3 space-y-3 space-y-reverse sm:space-y-0 pt-6 border-t border-slate-200">
            <Button 
              type="button" 
              variant="outline" 
              onClick={onSuccess}
              className="w-full sm:w-auto h-10 border-slate-300 text-slate-700 hover:bg-slate-50"
            >
              Cancel
            </Button>
            <Button 
              type="submit"
              className="w-full sm:w-auto h-10 bg-teal-600 hover:bg-teal-700 text-white shadow-sm"
              disabled={mutation.isPending}
            >
              {mutation.isPending && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
              {isEditing ? "Update Employee" : "Create Employee"}
            </Button>
          </div>
        </form>
      </Form>
    </div>
  );
}

